export class AppComponent {
  title = 'observables';
  
  minhaObservable(nome: string) : Observable<string>{
    return new Observable(Subscriber => {

      if(nome === 'Eduardo')
      {
        Subscriber.next('pronto'  + nome);
        Subscriber.next('pronto de novo ?' + nome);
        setTimeout(() => {
        Subscriber.next(nome + ' olha o delay' + nome);
      }, 4000);
      }
      else {
       Subscriber.error('olha o erro ai');   
      }   
    });
  }

  ngOnInit(): void {
    this.minhaObservable('')
      .subscribe(result => console.log(result),
      // tratando o erro
      erro => console.log(erro));

      // observador
      const observer = {
        next: valor => console.log('Next', valor),
        erro: erro => console.log('Erro', erro),
        complete: () => console.log('acabou')
      }
  }
}
